document.addEventListener("DOMContentLoaded",function(){console.log("Checking image loading..."),document.querySelectorAll('img[src$=".svg"]').forEach(o=>{console.log(`SVG image: ${o.src}, alt: ${o.alt}, display: ${window.getComputedStyle(o).display}, visibility: ${window.getComputedStyle(o).visibility}, width: ${o.offsetWidth}, height: ${o.offsetHeight}`),o.onerror=function(){console.error(`Failed to load SVG: ${o.src}`)},o.onload=function(){console.log(`Successfully loaded SVG: ${o.src}`)}}),document.querySelectorAll('img:not([src$=".svg"])').forEach(o=>{o.onerror=function(){console.error(`Failed to load image: ${o.src}`)}})});
