document.addEventListener("DOMContentLoaded",function(){console.log("SVG Test Page Loaded"),document.querySelectorAll('img[src$=".svg"]').forEach(e=>{e.onerror=function(){console.error(`SVG load error: ${e.src}`),e.style.border="3px solid red";const o=document.createElement("p");o.textContent=`Error loading: ${e.src}`,o.style.color="red",e.parentNode.appendChild(o)},e.onload=function(){console.log(`SVG loaded successfully: ${e.src}`),e.style.border="3px solid green"}}),document.querySelectorAll('object[data$=".svg"]').forEach(e=>{e.onerror=function(){console.error(`SVG object load error: ${e.data}`),e.style.border="3px solid red"},e.onload=function(){console.log(`SVG object loaded successfully: ${e.data}`),e.style.border="3px solid green"}})});
